var $=function(a){if("function"==typeof a)document.addEventListener("DOMContentLoaded",a);else{var b=Object.create($.fn),c;if("object"==typeof a)b[0]=a,b.length=1;else if(c=a.match(/<(.+)>/))b[0]=document.createElement(c[1]),b.length=1;else{a=document.querySelectorAll(a);for(c=0;c<a.length;c++)b[c]=a[c];b.length=a.length}return b}};
$.fn=Object.create(Array.prototype,{on:{value:function(a,b){this.forEach(function(c){c.addEventListener(a,b)});return this}},off:{value:function(a,b){this.forEach(function(c){c.removeEventListener(a,b)});return this}},get:{value:function(a){return this[a||0]}},first:{get:function(){return this[0]}},addClass:{value:function(a){this.forEach(function(b){b.className=b.className+" "+a});return this}},removeClass:{value:function(a){this.forEach(function(b){b.className=b.className.replace(a,"")});return this}},
hasClass:{value:function(a){return""!=this.first.className&&(new RegExp("\\b"+a+"\\b")).test(this.first.className.toLowerCase())}}});
